import React, { useState } from 'react';

const API_ROOT =
    'https://strangers-things.herokuapp.com/api/2010-USD-RM-WEB-PT/users';

const API_LOGIN = `${API_ROOT}/login`
const API_REGISTER = `${API_ROOT}/register`

const Account = ({ action, setToken }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const isLogin= action === 'login';
    const title = isLogin ? 'Login' : 'Create Account';
    const oppositeTitle = isLogin ? 'Create Account' : 'Login';
    const oppositeAction = isLogin ? 'register' : 'login';
    // const history = useHistory();

    const handleSubmit = async (event) => {
        event.preventDefault();
        console.log(`Username is: ${username}`);
        console.log(`Password is: ${password}`);
        const response = await fetch(API_REGISTER, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                user: {
                    username: username,
                    password: password,
                },
            }),
        }).catch((error) => {
            console.error(error);
        });
        const { data } = await response.json().catch((error) => {
            console.error(error);
        });
        console.log('data: ', data);
        if (data) {
            setToken(data.token);
        }
    };

    return (
        <div className= 'account-form'>
        <h1> {title} </h1>
            <form onSubmit= {handleSubmit}>
                <label>Username:</label>
                <input 
                type='text' 
                required
                onChange = {(event) => 
                    setUsername(event.target.value)
                }
                ></input>
                <label>Password:</label>
                <input 
                type='password' 
                required
                onChange = {(event) => 
                    setPassword(event.target.value)
                }
                ></input>
                <label>Re-enter Password:</label>
                <input type='password' required
                onChange = {(event) => 
                    setPassword(event.target.value)
                }
                ></input>
                <button type='submit'
                > {title} </button>
            </form>
            <link to= {`/${oppositeAction}`}> {oppositeTitle} </link> 
        </div>
    );
 }
export default Account;
