import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';


const API_ROOT =
    'https://strangers-things.herokuapp.com/api/2010-USD-RM-WEB-PT/users';

const API_LOGIN = `${API_ROOT}/login`
const API_REGISTER = `${API_ROOT}/register`
const API_USER = `${API_ROOT}/me`

const Account = ({ action, setToken }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const isLogin= action === 'login';
    const title = isLogin ? 'Login' : 'Create Account';
    const oppositeTitle = isLogin ? 'Create Account' : 'Login';
    const oppositeAction = isLogin ? 'register' : 'login';
    const actionURL= isLogin ? API_LOGIN : API_REGISTER;
    const history= useHistory ();

    const handleSubmit = async (event) => {
        event.preventDefault();
        console.log(`Username is: ${username}`);
        console.log(`Password is: ${password}`);
        const response = await fetch(actionURL, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                user: {
                    username: username,
                    password: password,
                },
            }),
        }).catch((error) => {
            console.error(error);
        });
        const { data } = await response.json().catch((error) => {
            console.error(error);
        });
        console.log('data: ', data);
        const token = data.token;
        if (token) {
            setToken(token);
            const userResponse = fetch (API_USER, {
                headers: {
                    'Content-Type' : 'application/json',
                    Authorization : `Bearer ${token}`,
                }
            }).catch ((error => 
                console.error(' Error getting user data', error))
                );
                const {data } = await userResponse.json().catch((error) =>
                console.error('Error parsing json', error));

            history.push('/')
        }
    };

    return (
        <div className= 'account-form'>
        <h1> {title} </h1>
            <form onSubmit= {handleSubmit}>
                <label>Username:</label>
                <input 
                type='text' 
                required
                onChange = {(event) => 
                    setUsername(event.target.value)
                }></input>
                <label>Password:</label>
                <input 
                type='password' 
                required
                onChange = {(event) => 
                    setPassword(event.target.value)
                }></input>
                  <label>Re-enter Password:</label>
                <input type='password' required
                onChange = {(event) => 
                    setPassword(event.target.value)
                 } ></input>
                <button type='submit'> {title} </button> 
                </form>

            <Link to= {`/${oppositeAction}`}> {oppositeTitle} </Link>
        </div>
    );
 }
export default Account;
