import React, { useState, useEffect } from 'react';
import { Route } from 'react-router-dom';
import { Account } from './';
import { callApi } from '../api';


const fetchUserData = async (token) => {
    const { data } = await callApi({
        url: '/users/me',
        token,
    });

    return data;
};


const App = () => {
    const [token, setToken] = useState('');
    const [userData, setUserData]= useState({})
    console.log(`Token is: ${token}`);

    useEffect(async () => {
        if (!token) {
            setToken(localStorage.getItem('token'));
            return;
        }
        const data = await fetchUserData(token);
        if (data && data.username) {
            setUserData(data);
        }
    }, [token]);

    return (
        <>
         <div className='tagline'>Stranger's Thing: Where Stranger is Better!</div>

        <Route exact path='/'>
           {userData ? `Welcome Back, ${userData.username}! `
           : `Welcome`
            }
        </Route>
        <Route path= '/login'>
        <Account action= 'login' setToken={setToken} setUserData= {setUserData}/>
        </Route>
        <Route path ='/register'>
        <Account action= 'register' setToken={setToken} setUserData= {setUserData} />
        </Route>
        </>
    );
};

export default App;
