import { callApi } from '../api';


const NewPost = () =>{


    const handleOnSubmit = async (event) => {
        event.preventDefault();

        const data = await callApi({
            url: `/users/me`,
            body: JSON.stringify({user: {username, password}}),
            method: 'POST',
        })

        console.log(`newpost: ${data}`)
    }

    return (

        <form onSubmit = {handleOnSubmit}>        
        <input type= 'text' placeholder= 'Title' required></input>
        <textarea placeholder= 'Description' rows="2" cols="15" required></textarea>
        <input type='number' placeholder= 'Price' required></input>
        <input type= 'text' placeholder= 'Location' required></input>
        <select>
        <option value="" disabled selected>Able to Deliver?</option>
        <option value="Yes">Yes</option>
        <option value="No">No</option>
        </select>

        <button>Add Post</button>
        </form>

    )

}

export default NewPost;
