import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

const postMatches = (post, searchTerm) =>{
    const searchTermLowercase= searchTerm.toLowerCase();
    const{ 
        description, 
        location, 
        title, 
        author: { username }
    } = post;

    const toMatch = [description, location, title, username];
    for (const field of toMatch){
        if (field.toLowerCase().includes(searchTermLowercase)){
            return true;
        }
    }
}

const List = ({ posts }) => {
    const history = useHistory();
    const [searchTerm, updateSearchTerm] = useState ('')

    const postDisplay = post.filter((post) => postMatches(post, searchTerm))

    console.log(postDisplay)
    return (
        <>
            <h2>Posts</h2>
            <input
            type = 'text'
            placeholder= 'Search for posts'
            value= {searchTerm}
            onChange= {(event)=> {
                updateSearchTerm(event.target.value);
            }} 
            />
            <div className='post-list'>
            {posts.map((post) => (
                <div key={post._id} className= 'post'>
                    <h5>{post.title}</h5>
                    <div>Posted by: {post.author.username}</div>
                    <div>Description: {post.description} </div>
                    <button
                        onClick={() => {
                            history.push(`/posts/${post._id}`);
                        }}>View Post</button>
                </div>
            ))}
            </div>
        </>
    );
};

export default List;
