import React from 'react';
import { useHistory } from 'react-router-dom';


const Inbox = ( { userData, token}) =>{

    const myInbox= userData.messages
    const userId= userData._id
    const sentMessages = myInbox.filter((message) => {
        return userId === message.fromUser._id});
    const receivedMessages = myInbox.filter((message) =>{
        return userId !== message.fromUser._id
    })


    console.log ('sent',sentMessages)


    console.log('received', receivedMessages)

// console.log(`user id: ${userData._id} message user id :${message.fromUser._id}`)




const history = useHistory();

    return (
        <div className='messages-display'>
      { myInbox ?


      (myInbox.map((message, index) => {
        <h2>Messages Received:</h2>
        if( userId !== message.fromUser._id){
            return(
        <div key= {index} className='message-received'>        
        <h3>From: {message.fromUser.username}</h3>
        <h3>Message: {message.content}</h3>
        <h3>Orginial Post: {message.post.title} </h3>
        <button
        onClick={()=>{
            history.push(`/posts/${message.post._id}`)
        }}
        >View Original Post</button>
        </div>
        )    
        } 
        <h2>Messages I sent:</h2>
        return (<div key= {index} className='message'>    
        <h3>Message: {message.content}</h3>
        <h3>Orginial Post: {message.post.title} </h3>
        <button
        onClick={()=>{
            history.push(`/posts/${message.post._id}`)
        }}
        >View Original Post</button>
        </div>)
    })): 'No Messages'
    } 
    </div>
    )

}

export default Inbox ;
